var url = bundle.spaceLocation();
var recurrence_exists = K('field[Recurrence Submission Id]').value() !== null ? true : false;
if ( recurrence_exists ) {
  url += '/app/datastore/submissions/' + K('field[Recurrence Submission Id]').value();
} else {
  url += '/app/datastore/forms/robot-definitions';
}

K.load({
  path: url + "?values[Embedded]=Embedded",
  container: '#recurrenceModal .modal-body',
  loaded: function(form) { 
    // Set values ... but only for defaults the first time you open.
    if ( !recurrence_exists ) {
      form.select( 'field[Robot Name]' ).value( 'OnSolve Integration - ' + K('field[Name]').value() );
      form.select( 'field[Description]' ).value( 'This robot was created from a submission to the OnSolve Integration form: \'' + K('form').name() + '\'.  It is normally not updated directly from the Robot Definition form, but through updates to the OnSolve Integration form.' );
      form.select( 'field[Category]' ).value( 'OnSolve Integration' );
      //form.select( 'field[Status]' ).value( 'Active' );
      //form.select( 'field[Task Tree]' ).value( 'OnSolve Integration - ' + K('form').name() );
      form.select( 'field[Task Tree]' ).value( K('field[Polling Task Tree]').value() );
      form.select( 'field[Runtime Inputs]' ).value( '{"Integration Configuration Submission Id":"' + K('submission').id() + '"}' );
      form.select( 'field[Notify Upon Each Run Completion]' ).value( 'No' );
      form.select( 'field[Start Date]' ).value( (new Date).toISOString() );
    } else {
      // passing values on the parameter line (i.e., to set the embedded field flag) only works on create, so you pass it as a value after load on existing records.
      form.select( 'field[Embedded]' ).value( 'Embedded' );
    }
  },
  created: function(data) { 
    K('field[Recurrence Submission Id]').value( data['submission']['id'] );
    K('field[Recurrence Description]').value( data['submission']['values']['Recurrence Description'] );
    K('field[Recurrence Status]').value( data['submission']['values']['Status'] );
    $('#recurrenceModal').modal('hide');
  },
  updated: function(data) { 
    K('field[Recurrence Description]').value( data['submission']['values']['Recurrence Description'] );
    K('field[Recurrence Status]').value( data['submission']['values']['Status'] );
    $('#recurrenceModal').modal('hide');
  },
  completed: function(data, action) {  }
});

