integration.getPlaceholders = function() {
  // Clear placeholders
  $('div.placeholders').empty();

  // Retrieve Notification Placeholders
  K('bridgedResource[Onsolve Notification Placeholders]').load({
    success: function(bridgedData) {
      if (bridgedData.length > 0) {
        K('section[Placeholders Container]').show();
      }
      $.each( bridgedData, function( k, v ) {
        var help_array = [];
        help_array.push( 'Optional: ' + v['Optional'] );

        //if checkbox, value must result in true or false
        if ( v['Placeholder Type'] === "checkbox" ) {
          help_array.push( "Note: When populated, field must evaluate to 'true' or 'false'" );
        };

        //if multiple choice, value must result in one of these options
        if ( v['Placeholder Type'] === "multipleChoice" ) {
          var metaData = JSON.parse( v['Meta Data'] );
          if ( metaData['Options'] ) {
            var optionsArray = [];
            $.each( metaData['Options'], function( i, v ) {
              optionsArray.push( v['value'] );
            });
            help_array.push( "Note: When populated, field must evaluate to one of these values: " + optionsArray.join(", ") );
          };
        };
        var help_el = '<small id="' + v['Id'] + '-help" class="form-text text-muted">' + help_array.join("; ") + '</small>';
        var ph_container_el = $('<div class="placeholder"></div>');
        var ph_el = $('<div class="form-group"></div>');
        ph_el.append('<label for="' + v['Id'] + '">'+ v['Placeholder Name'] + '</label>');
        ph_el.append('<input type="text" data-placeholdername="' + v['Placeholder Name'] + '" class="form-control" id="' + v['Id'] + '" placeholder="' + v['Default Value'] + '">');
        ph_el.append(help_el);
        ph_container_el.append(ph_el);
        $('div.placeholders').append(ph_container_el);

        // Populate the placeholder values with data.
        $.each( JSON.parse( K('field[Placeholder Data]').value() || "[]" ), function( i, v ) {
          $( 'div.placeholders input[data-placeholdername="' + v['name'] + '"]' ).val( v['value'] );
        });
      }); 
    }
  });
  // Render
  // Retrieve Notification Placeholder Values and populate
}
integration.savePlaceholders = function() {
  // Write to Notification Placeholder Values
  var placeholderData=[];
  $('.placeholders .placeholder input').each( function() {
    var phName = $(this).data('placeholdername');
    var phValue = $(this).val();
    placeholderData.push( {"name": phName, "value": phValue} )
  })
  K('field[Placeholder Data]').value( JSON.stringify(placeholderData) );
}

