integration.applyEscalationRecipientData = function() {
  // Set fields read only
  /*
  K('field[Max Response Time]').disable();
  K('field[Individuals]').disable();
  K('field[Groups]').disable();
  K('field[Schedules]').disable();
  */

  // store information

  var escalationLevels = JSON.parse( K('field[Recipients JSON]').value() || "{}" ),
      maxResponse = K('field[Max Response Time]').value(),
      individuals = K('field[Individuals]').value(),
      groups = K('field[Groups]').value(),
      schedules = K('field[Schedules]').value();
      //oncall = K('field[OnCall Support Group Id]').value();

  if ( (!individuals || individuals.trim() === "") && (!groups || groups.trim() === "") && (!schedules || schedules.trim() === "") ) {
    // delete element from hash in javascript
    delete escalationLevels[ K('field[Escalation Level]').value() ];
  } else {
    // only populate the values that have content
    escalationLevels[ K('field[Escalation Level]').value() ] = {}
    if (maxResponse && maxResponse.trim() !== "") {
      escalationLevels[ K('field[Escalation Level]').value() ]["maxResponse"] = maxResponse;
    }
    if (individuals && individuals.trim() !== "") {
      escalationLevels[ K('field[Escalation Level]').value() ]["individuals"] = individuals;
    }
    if (groups && groups.trim() !== "") {
      escalationLevels[ K('field[Escalation Level]').value() ]["groups"] = groups;
    }
    if (schedules && schedules.trim() !== "") {
      escalationLevels[ K('field[Escalation Level]').value() ]["schedules"] = schedules;
    }
  }

  K('field[Recipients JSON]').value( JSON.stringify(escalationLevels) );

  integration.populateConfiguredEscalationLevels();
}

integration.consolidateRecipientData = function() {
  var maxResponse = K('field[Max Response Time]').value(),
      individuals = K('field[Individuals]').value(),
      groups = K('field[Groups]').value(),
      schedules = K('field[Schedules]').value(),
      recipientData = {};

  // only populate the values that have content
	if (maxResponse && maxResponse.trim() !== "") {
    recipientData["maxResponse"] = maxResponse;
  }
  if (individuals && individuals.trim() !== "") {
    recipientData["individuals"] = individuals;
  }
  if (groups && groups.trim() !== "") {
    recipientData["groups"] = groups;
  }
  if (schedules && schedules.trim() !== "") {
    recipientData["schedules"] = schedules;
  }

  K('field[Recipients JSON]').value( JSON.stringify(recipientData) );
}

integration.populateRecipientData = function() {
  if( K('field[Notification Method]').value() === "broadcastInfo" || K('field[Notification Method]').value() === "bulletinBoardInfo" ) {
    var recipientData = JSON.parse( K('field[Recipients JSON]').value() || "{}" );
    K('field[Individuals]').value( recipientData['individuals'] );
    K('field[Groups]').value( recipientData['groups'] );
    K('field[Schedules]').value( recipientData['schedules'] );
  }
  // Set the escalation level to 1, to display the first set of recipients (if any)
  if ( K('field[Notification Method]').value() === "firstResponseInfo" || K('field[Notification Method]').value() === "calloutInfo" ) {
    K('field[Escalation Level]').value('Escalation Level 1');
    //Populate the Escalations currently identified
    integration.populateConfiguredEscalationLevels();
  }
}

integration.populateConfiguredEscalationLevels = function() {
  var recipients = JSON.parse( K('field[Recipients JSON]').value() || "{}" ),
      configuredLevels = [];
  // Clear current listing
  $('#escalation-levels').empty();

  // Loop over configured listing and populate
  $.each( recipients, function( k, v ) {
    configuredLevels.push( '<span class="configured-level">' + k + '</span>' );
  })

  if (configuredLevels.length > 0) {
    $('#escalation-levels').html(configuredLevels.join(", "));
  } else {
    $('#escalation-levels').html('<span class="configured-level">None</span>');
  }
}

integration.getRecurrenceStatus = function() {
