    K('bridgedResource[Robot by Id]').load({
      success: function(bridgedData) {
        K('field[Recurrence Status]').value(bridgedData['Status']);
      }
    })
  }
}

integration.createJsonInputs = function(jsonField, container, allowDelete) {
  $('#'+container).on('click','.delete', function() {
    var json_el = $(this).closest('.json-container'),
        json_el_key = json_el.find('.json-key').val();
    result = confirm("Are you sure you want to remove the " + json_el_key + " parameter?");
    if ( result === true ) {
        json_el.remove();
    }
  });

  $.each( JSON.parse( K('field['+jsonField+']').value() || "{}" ), function( k, v ) {
    var container_el = $('<div class="json-container"></div>'),
        el = $('<div class="form-group"></div>'),
        el_input_group = $('<div class="input-group">');
    //allow delete means users can edit the key value as well
    if ( allowDelete === true) {
      el_input_group.append('<input type="text" class="form-control json-key" value="' + k + '">');
    } else {
	    el_input_group.append('<input type="text" readonly="true" class="form-control json-key" value="' + k + '">');
    }
    el_input_group.append('<input type="text" class="form-control json-value" value="' + v + '">');
    if ( allowDelete === true) {
      el_input_group.append('<div class="input-group-append"><span class="input-group-text delete bg-danger"></i><i class="fa fa-close fa-fw"></i></span></div>');
    }
    container_el.append( el.append( el_input_group ) );
    $('#'+container).append( container_el );
  });
}

integration.addJsonInput = function(container, allowDelete) {
  var container_el = $('<div class="json-container"></div>'),
      el = $('<div class="form-group"></div>'),
      el_input_group = $('<div class="input-group">');
  el_input_group.append('<input type="text" class="form-control json-key" placeholder="key">');
  el_input_group.append('<input type="text" class="form-control json-value" placeholder="value">');
  if ( allowDelete === true) {
    el_input_group.append('<div class="input-group-append"><span class="input-group-text delete bg-danger"><i class="fa fa-close fa-fw"></i></span></div>');
  }
  container_el.append( el.append( el_input_group ) );
  $('#'+container).append( container_el );
}

integration.storeJsonInputs = function(jsonField, container) {
  //$.each( JSON.parse( K('field['+jsonField+']').value() || "{}" ), function( k, v ) {
  var obj = {};
  $('#' + container + ' div.json-container').each(function(){
    var key = $(this).find('input.json-key').val();
        value = $(this).find('input.json-value').val();
    obj[key] = value;
  });
  K('field['+jsonField+']').value( JSON.stringify( obj ) );
}


